    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Open popup
            document.querySelectorAll('.popup-button').forEach(function(btn) {
                btn.addEventListener('click', function(e) {
                    e.preventDefault();
                    const popupId = btn.id.replace('btn-', 'popup-');
                    const popup = document.getElementById(popupId);
                    popup.classList.remove('invisible', 'opacity-0');
                });
            });

            // Close popup
            document.querySelectorAll('.popup-close').forEach(function(btn) {
                btn.addEventListener('click', function() {
                    const popup = btn.closest('.popup-main');
                    popup.classList.add('invisible', 'opacity-0');
                });
            });

            // Toggle password visibility
            document.querySelectorAll('.togglePassword').forEach(button => {
                const input = button.previousElementSibling;
                const showIcon = button.querySelector('.showIcon');
                const hideIcon = button.querySelector('.hideIcon');
                button.addEventListener('click', () => {
                    const visible = input.type === 'text';
                    input.type = visible ? 'password' : 'text';
                    showIcon.classList.toggle('hidden', !visible);
                    hideIcon.classList.toggle('hidden', visible);
                });
            });

            // AJAX form submission
            $('#formAccountSettings').on('submit', function(e) {
                e.preventDefault();
                let form = $(this);
                let formData = new FormData(this);

                $.ajax({
                    url: form.attr('action'),
                    method: 'POST',
                    data: formData,
                    processData: false,
                    contentType: false,
                    success: function() {
                        Swal.fire({
                            title: 'Success!',
                            text: 'Password updated successfully.',
                            icon: 'success',
                            timer: 2000,
                            showConfirmButton: false
                        }).then(() => window.location.reload());
                    },
                    error: function(xhr) {
                        if (xhr.status === 422) {
                            const errors = xhr.responseJSON.errors;
                            $('#formErrors').html('').removeClass('d-none');
                            $.each(errors, function(key, value) {
                                $('#formErrors').append(`<p>${value[0]}</p>`);
                                $(`[name="${key}"]`).addClass('border-red-500');
                            });
                        } else {
                            Swal.fire('Error', 'Something went wrong.', 'error');
                        }
                    }
                });
            });
        });
    </script>
